/* This hangs MPW IIGS ORCA/C 1.0.1. */voidjk_reverse_memory_range_exclusive(    void * void_start,    void * void_end    );voidjk_reverse_memory_byte_count(    void * p_void,    unsigned long count    ){    jk_reverse_memory_range_exclusive(p_void, count + (unsigned char*)p_void);}voidjk_reverse_memory_range_inclusive(    void * void_start,    void * void_end    ){	jk_reverse_memory_range_exclusive(void_start, 1 + (unsigned char*)void_end);}